import React, { useState } from "react";

function highlightColor(ratio: number) {
  if (ratio < 1) return "green";
  if (ratio > 1) return "red";
  return "grey";
}

const App = () => {
  const [prUrl, setPrUrl] = useState("");
  const [workloadCode, setWorkloadCode] = useState("");
  const [running, setRunning] = useState(false);
  const [result, setResult] = useState(null);
  const [error, setError] = useState(null);
  const [dockerStatus, setDockerStatus] = useState(null);
  const [checkingDocker, setCheckingDocker] = useState(false);

  const copyToClipboard = async (text) => {
    try {
      await navigator.clipboard.writeText(text);
      // 可以添加一个临时的成功提示
    } catch (err) {
      console.error('Failed to copy: ', err);
    }
  };

  const hasExtraOutput = (perfOutput) => {
    if (!perfOutput || perfOutput.trim() === '') return false;

    // 检查是否只有标准的四行输出
    const lines = perfOutput.trim().split('\n').filter(line => line.trim() !== '');

    console.log('hasExtraOutput check:', {
      perfOutput: perfOutput,
      lines: lines,
      lineCount: lines.length
    });

    // 标准输出应该只有4行：+ python /tmp/workload.py, Mean:, Std Dev:, + echo
    if (lines.length === 4) {
      const hasPythonCmd = lines.some(line => line.includes('+ python /tmp/workload.py'));
      const hasMean = lines.some(line => line.startsWith('Mean:'));
      const hasStdDev = lines.some(line => line.startsWith('Std Dev:'));
      const hasEcho = lines.some(line => line.includes('+ echo'));

      console.log('Standard format check:', {
        hasPythonCmd,
        hasMean,
        hasStdDev,
        hasEcho,
        allMatch: hasPythonCmd && hasMean && hasStdDev && hasEcho
      });

      // 如果所有四行都符合标准格式，则没有额外输出
      if (hasPythonCmd && hasMean && hasStdDev && hasEcho) {
        return false;
      }
    }

    // 如果行数不是4，或者四行中有不符合标准的，说明有额外输出
    return true;
  };

  const clearAllInputs = () => {
    setPrUrl("");
    setWorkloadCode("");
    // 不清除 result 和 error，保持输出显示
  };

  const checkDockerStatus = async () => {
    setCheckingDocker(true);
    try {
      const res = await fetch("http://localhost:5679/check_docker");
      const data = await res.json();
      setDockerStatus(data);
    } catch (e) {
      setDockerStatus({
        installed: false,
        running: false,
        permissions: false,
        message: "Cannot connect to Docker check service",
        setup_instructions: ["Please ensure Docker permission check service is running"]
      });
    } finally {
      setCheckingDocker(false);
    }
  };

  const handleRun = async () => {
    if (!dockerStatus?.permissions) {
      setError("Docker permissions not configured, please check Docker status first");
      return;
    }

    setRunning(true);
    setResult(null);
    setError(null);
    try {
      const res = await fetch("http://localhost:5678/run_benchmark", {
        method: "POST",
        headers: {"Content-Type": "application/json"},
        body: JSON.stringify({
          pr_url: prUrl,
          workload_code: workloadCode,
        }),
      });
      const data = await res.json();
      setResult(data);
    } catch (e) {
      setError("Run failed: " + e);
    } finally {
      setRunning(false);
    }
  };

  return (
    <div style={{
      minHeight: "100vh",
      background: "linear-gradient(135deg, #A51C30 0%, #8B0000 50%, #660000 100%)",
      fontFamily: "'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif",
      position: "relative",
      overflowX: "hidden",
      overflowY: "auto",
      backgroundAttachment: "fixed",
      overscrollBehavior: "none"
    }}>

      {/* Header with Logo */}
      <div style={{
        background: "white",
        borderBottom: "1px solid rgba(165, 28, 48, 0.2)",
        padding: "20px 0",
        boxShadow: "0 4px 30px rgba(165, 28, 48, 0.15)",
        position: "relative",
        zIndex: 10
      }}>
        <div style={{maxWidth: 1200, margin: "0 auto", padding: "0 20px", display: "flex", alignItems: "center", justifyContent: "space-between"}}>
          <div style={{display: "flex", alignItems: "center", gap: "20px", marginLeft: "-120px"}}>
            <div style={{
              width: "236px",
              height: "80px",
              borderRadius: "12px",
              overflow: "hidden",
              display: "flex",
              alignItems: "center",
              justifyContent: "center",
              background: "white"
            }}>
              <img 
                src="/Edge Computing Lab Logo.png" 
                alt="Edge Computing Lab Logo"
                style={{
                  maxWidth: "100%",
                  maxHeight: "100%",
                  objectFit: "contain",
                  width: "auto",
                  height: "auto"
                }}
              />
            </div>
            <div>
              <h1 style={{
                margin: 0,
                fontSize: "32px",
                fontWeight: "800",
                color: "#A51C30",
                letterSpacing: "-0.5px"
              }}>
                Performance Benchmark Platform
              </h1>
              <p style={{
                margin: "4px 0 0 0",
                fontSize: "20px",
                color: "#666",
                fontWeight: "600"
              }}>
                Edge Computing Lab
              </p>
              <p style={{
                margin: "2px 0 0 0",
                fontSize: "12px",
                color: "#A51C30",
                fontWeight: "600",
                fontStyle: "italic"
              }}>
                Harvard John A. Paulson School of Engineering and Applied Sciences
              </p>
            </div>
          </div>
        </div>
      </div>

      {/* Main Content */}
      <div style={{maxWidth: 1200, margin: "0 auto", padding: "40px 20px", position: "relative"}}>
        {/* Docker Status Check Section */}
        <div style={{
          background: "rgba(255, 255, 255, 0.95)",
          backdropFilter: "blur(20px)",
          borderRadius: "20px",
          padding: "30px",
          marginBottom: "30px",
          boxShadow: "0 8px 40px rgba(165, 28, 48, 0.15)",
          border: "1px solid rgba(165, 28, 48, 0.1)"
        }}>
          <h3 style={{
            margin: "0 0 20px 0",
            fontSize: "22px",
            fontWeight: "700",
            color: "#A51C30",
            display: "flex",
            alignItems: "center",
            gap: "10px"
          }}>
            <span style={{fontSize: "24px"}}>🔧</span>
            Docker Status Check
          </h3>
          <button 
            onClick={checkDockerStatus} 
            disabled={checkingDocker}
            style={{
              padding: "14px 28px",
              marginBottom: "20px",
              background: checkingDocker ? "#e0e0e0" : "linear-gradient(45deg, #A51C30, #8B0000)",
              color: "white",
              border: "none",
              borderRadius: "12px",
              fontSize: "15px",
              fontWeight: "600",
              cursor: checkingDocker ? "not-allowed" : "pointer",
              transition: "all 0.3s ease",
              boxShadow: checkingDocker ? "none" : "0 6px 20px rgba(165, 28, 48, 0.4)",
              border: "1px solid rgba(165, 28, 48, 0.2)"
            }}
          >
            {checkingDocker ? "Checking..." : "Check Docker Status"}
          </button>

          {dockerStatus && (
            <div>
              <div style={{marginBottom: "15px"}}>
                <strong style={{color: "#333"}}>Status: </strong>
                <span style={{
                  color: dockerStatus.permissions ? "#059669" : "#DC2626",
                  fontWeight: "600",
                  padding: "6px 14px",
                  borderRadius: "20px",
                  background: dockerStatus.permissions ? "rgba(5, 150, 105, 0.1)" : "rgba(220, 38, 38, 0.1)",
                  fontSize: "14px",
                  border: "1px solid",
                  borderColor: dockerStatus.permissions ? "rgba(5, 150, 105, 0.3)" : "rgba(220, 38, 38, 0.3)"
                }}>
                  {dockerStatus.permissions ? "✅ Normal" : "❌ Configuration Required"}
                </span>
              </div>
              <div style={{marginBottom: "15px", color: "#666"}}>
                <strong>Message: </strong>{dockerStatus.message}
              </div>
              {dockerStatus.setup_instructions.length > 0 && (
                <div style={{
                  background: "rgba(165, 28, 48, 0.05)",
                  padding: "20px",
                  borderRadius: "12px",
                  border: "1px solid rgba(165, 28, 48, 0.2)"
                }}>
                  <strong style={{color: "#A51C30"}}>Setup Instructions:</strong>
                  <ul style={{margin: "10px 0", paddingLeft: "20px", color: "#666"}}>
                    {dockerStatus.setup_instructions.map((instruction, index) => (
                      <li key={index} style={{marginBottom: "5px"}}>{instruction}</li>
                    ))}
                  </ul>
                </div>
              )}
            </div>
          )}
        </div>

        {/* Main Form */}
        <div style={{
          background: "rgba(255, 255, 255, 0.95)",
          backdropFilter: "blur(20px)",
          borderRadius: "20px",
          padding: "40px",
          marginBottom: "30px",
          boxShadow: "0 8px 40px rgba(165, 28, 48, 0.15)",
          border: "1px solid rgba(165, 28, 48, 0.1)"
        }}>
          <div style={{
            display: "flex",
            justifyContent: "space-between",
            alignItems: "center",
            marginBottom: "30px"
          }}>
            <h2 style={{
              margin: 0,
              fontSize: "28px",
              fontWeight: "800",
              color: "#A51C30",
              letterSpacing: "-0.5px"
            }}>
              Performance Benchmark Configuration
            </h2>
            <button 
              onClick={clearAllInputs}
              disabled={running}
              style={{
                padding: "10px 20px",
                background: running ? "#e0e0e0" : "linear-gradient(45deg, #666, #888)",
                color: "white",
                border: "none",
                borderRadius: "10px",
                fontSize: "14px",
                fontWeight: "600",
                cursor: running ? "not-allowed" : "pointer",
                transition: "all 0.3s ease",
                boxShadow: running ? "none" : "0 4px 15px rgba(0, 0, 0, 0.2)"
              }}
              onMouseOver={(e) => !running && (e.target.style.background = "linear-gradient(45deg, #555, #777)")}
              onMouseOut={(e) => !running && (e.target.style.background = "linear-gradient(45deg, #666, #888)")}
            >
              Clear All
            </button>
          </div>

          <div style={{display: "grid", gap: "25px"}}>
            <div>
              <label style={{
                display: "block",
                marginBottom: "10px",
                fontSize: "15px",
                fontWeight: "600",
                color: "#A51C30"
              }}>
                GitHub PR URL:
              </label>
              <input 
                type="text"
                style={{
                  width: "100%",
                  padding: "14px 18px",
                  border: "2px solid rgba(165, 28, 48, 0.2)",
                  borderRadius: "12px",
                  fontSize: "14px",
                  transition: "all 0.3s ease",
                  background: running ? "#f9fafb" : "white",
                  opacity: running ? 0.6 : 1,
                  outline: "none"
                }}
                value={prUrl} 
                onChange={e=>setPrUrl(e.target.value)} 
                disabled={running}
                onFocus={(e) => e.target.style.borderColor = "#A51C30"}
                onBlur={(e) => e.target.style.borderColor = "rgba(165, 28, 48, 0.2)"}
              />
            </div>

            <div>
              <label style={{
                display: "block",
                marginBottom: "10px",
                fontSize: "15px",
                fontWeight: "600",
                color: "#A51C30"
              }}>
                Workload Code:
              </label>
              <textarea 
                style={{
                  width: "100%",
                  minHeight: "200px",
                  padding: "14px 18px",
                  border: "2px solid rgba(165, 28, 48, 0.2)",
                  borderRadius: "12px",
                  fontSize: "14px",
                  fontFamily: "'Fira Code', 'Monaco', 'Consolas', monospace",
                  resize: "vertical",
                  transition: "all 0.3s ease",
                  background: running ? "#f9fafb" : "white",
                  opacity: running ? 0.6 : 1,
                  outline: "none"
                }}
                value={workloadCode} 
                onChange={(e) => setWorkloadCode(e.target.value)}
                disabled={running}
                onFocus={(e) => e.target.style.borderColor = "#A51C30"}
                onBlur={(e) => e.target.style.borderColor = "rgba(165, 28, 48, 0.2)"}
              />
            </div>

            <div style={{textAlign: "center", marginTop: "20px"}}>
              <button 
                style={{
                  padding: "18px 50px",
                  background: running ? "#e0e0e0" : "linear-gradient(45deg, #A51C30, #8B0000)",
                  color: "white",
                  border: "none",
                  borderRadius: "16px",
                  fontSize: "18px",
                  fontWeight: "700",
                  cursor: running ? "not-allowed" : "pointer",
                  transition: "all 0.3s ease",
                  boxShadow: running ? "none" : "0 10px 30px rgba(165, 28, 48, 0.4)",
                  minWidth: "250px",
                  border: "1px solid rgba(165, 28, 48, 0.3)"
                }}
                onClick={handleRun} 
                disabled={running}
              >
                {running ? "⏳ Running..." : "🚀 Run Benchmark"}
              </button>
            </div>
          </div>
        </div>

        {/* Results Section */}
        {result && (
          <div style={{
            background: "rgba(255, 255, 255, 0.95)",
            backdropFilter: "blur(20px)",
            borderRadius: "20px",
            padding: "40px",
            marginBottom: "30px",
            boxShadow: "0 8px 40px rgba(165, 28, 48, 0.15)",
            border: "1px solid rgba(165, 28, 48, 0.1)"
          }}>
            <h3 style={{
              margin: "0 0 30px 0",
              fontSize: "26px",
              fontWeight: "800",
              color: "#A51C30",
              textAlign: "center",
              letterSpacing: "-0.5px"
            }}>
              📊 Benchmark Results
            </h3>

            <div style={{display: "grid", gap: "25px"}}>
              <div style={{
                background: "rgba(165, 28, 48, 0.05)",
                padding: "20px",
                borderRadius: "16px",
                border: "1px solid rgba(165, 28, 48, 0.2)"
              }}>
                <div style={{fontWeight: "600", color: "#A51C30", marginBottom: "8px"}}>
                  🐳 Image Name:
                </div>
                <div style={{fontFamily: "'Fira Code', monospace", fontSize: "14px"}}>
                  {result.image_tag}
                </div>
              </div>

              <div style={{
                background: "rgba(165, 28, 48, 0.05)",
                padding: "20px",
                borderRadius: "16px",
                border: "1px solid rgba(165, 28, 48, 0.2)"
              }}>
                <div style={{fontWeight: "600", color: "#A51C30", marginBottom: "12px", display: "flex", justifyContent: "space-between", alignItems: "center", flexWrap: "wrap", gap: "10px"}}>
                  <span style={{flex: "1", minWidth: "200px"}}>📋 Complete Docker Command:</span>
                  <button 
                    onClick={() => copyToClipboard(result.docker_command)}
                    style={{
                      padding: "6px 12px",
                      background: "#A51C30",
                      color: "white",
                      border: "none",
                      borderRadius: "6px",
                      fontSize: "12px",
                      cursor: "pointer",
                      transition: "all 0.2s ease",
                      whiteSpace: "nowrap"
                    }}
                    onMouseOver={(e) => e.target.style.background = "#8B0000"}
                    onMouseOut={(e) => e.target.style.background = "#A51C30"}
                  >
                    📋 Copy
                  </button>
                </div>
                <div style={{
                  background: "#f8fafc",
                  padding: "16px",
                  borderRadius: "12px",
                  fontSize: "13px",
                  fontFamily: "'Fira Code', monospace",
                  border: "1px solid #e2e8f0",
                  overflowX: "auto",
                  wordBreak: "break-all"
                }}>
                  {result.docker_command}
                </div>
                <div style={{fontSize: "12px", color: "#64748b", marginTop: "8px"}}>
                  Note: Replace &lt;REPLACE_ME&gt; with the full path to your workload.py file
                </div>
              </div>

              {result.download_info && (
                <div style={{
                  background: "rgba(165, 28, 48, 0.05)",
                  padding: "20px",
                  borderRadius: "16px",
                  border: "1px solid rgba(165, 28, 48, 0.2)"
                }}>
                  <div style={{fontWeight: "600", color: "#A51C30", marginBottom: "12px", display: "flex", justifyContent: "space-between", alignItems: "center", flexWrap: "wrap", gap: "10px"}}>
                    <span style={{flex: "1", minWidth: "200px"}}>📥 Image Download Info:</span>
                    <button 
                      onClick={() => copyToClipboard(result.download_info)}
                      style={{
                        padding: "6px 12px",
                        background: "#A51C30",
                        color: "white",
                        border: "none",
                        borderRadius: "6px",
                        fontSize: "12px",
                        cursor: "pointer",
                        transition: "all 0.2s ease",
                        whiteSpace: "nowrap"
                      }}
                      onMouseOver={(e) => e.target.style.background = "#8B0000"}
                      onMouseOut={(e) => e.target.style.background = "#A51C30"}
                    >
                      📋 Copy
                    </button>
                  </div>
                  <div style={{
                    background: "#f8fafc",
                    padding: "16px",
                    borderRadius: "12px",
                    fontSize: "13px",
                    fontFamily: "'Fira Code', monospace",
                    border: "1px solid #e2e8f0",
                    overflowX: "auto",
                    whiteSpace: "pre-wrap",
                    wordBreak: "break-all"
                  }}>
                    {result.download_info}
                  </div>
                </div>
              )}

              <div style={{display: "grid", gridTemplateColumns: "1fr 1fr", gap: "20px"}}>
                <div style={{
                  background: "rgba(165, 28, 48, 0.05)",
                  padding: "20px",
                  borderRadius: "16px",
                  border: "1px solid rgba(165, 28, 48, 0.2)"
                }}>
                  <div style={{fontWeight: "600", color: "#A51C30", marginBottom: "12px"}}>
                    📊 Initial Results:
                  </div>
                  {result.mean_before && result.std_before ? (
                    <div style={{
                      background: "white",
                      padding: "16px",
                      borderRadius: "12px",
                      border: "1px solid #e2e8f0"
                    }}>
                      <div style={{fontSize: "14px", fontWeight: "600"}}>
                        Mean: {result.mean_before.toFixed(6)}
                      </div>
                      <div style={{fontSize: "14px", fontWeight: "600"}}>
                        Std: {result.std_before.toFixed(6)}
                      </div>
                    </div>
                  ) : (
                    <div style={{color: "#DC2626", fontWeight: "bold"}}>
                      {result.before}
                    </div>
                  )}
                </div>

                <div style={{
                  background: "rgba(165, 28, 48, 0.05)",
                  padding: "20px",
                  borderRadius: "16px",
                  border: "1px solid rgba(165, 28, 48, 0.2)"
                }}>
                  <div style={{fontWeight: "600", color: "#A51C30", marginBottom: "12px"}}>
                    📊 Results After Patch:
                  </div>
                  {result.mean_after && result.std_after ? (
                    <div style={{
                      background: "white",
                      padding: "16px",
                      borderRadius: "12px",
                      border: "1px solid #e2e8f0"
                    }}>
                      <div style={{fontSize: "14px", fontWeight: "600"}}>
                        Mean: {result.mean_after.toFixed(6)}
                      </div>
                      <div style={{fontSize: "14px", fontWeight: "600"}}>
                        Std: {result.std_after.toFixed(6)}
                      </div>
                    </div>
                  ) : (
                    <div style={{color: "#DC2626", fontWeight: "bold"}}>
                      {result.after}
                    </div>
                  )}
                </div>
              </div>

              {/* 显示完整的PERF_START到PERF_END内容（如果有额外输出） */}
              {(result.perf_output_before && hasExtraOutput(result.perf_output_before)) && (
                <div style={{
                  background: "rgba(165, 28, 48, 0.05)",
                  padding: "20px",
                  borderRadius: "16px",
                  border: "1px solid rgba(165, 28, 48, 0.2)"
                }}>
                  <div style={{fontWeight: "600", color: "#A51C30", marginBottom: "12px", display: "flex", justifyContent: "space-between", alignItems: "center"}}>
                    <span>📋 Initial Results (Full Output):</span>
                    <button 
                      onClick={() => copyToClipboard(result.perf_output_before)}
                      style={{
                        padding: "6px 12px",
                        background: "#A51C30",
                        color: "white",
                        border: "none",
                        borderRadius: "6px",
                        fontSize: "12px",
                        cursor: "pointer",
                        transition: "all 0.2s ease"
                      }}
                      onMouseOver={(e) => e.target.style.background = "#8B0000"}
                      onMouseOut={(e) => e.target.style.background = "#A51C30"}
                    >
                      📋 Copy
                    </button>
                  </div>
                  <div style={{
                    background: "white",
                    padding: "16px",
                    borderRadius: "12px",
                    fontSize: "13px",
                    fontFamily: "'Fira Code', monospace",
                    border: "1px solid #e2e8f0",
                    overflowX: "auto",
                    whiteSpace: "pre-wrap",
                    maxHeight: "300px",
                    overflowY: "auto"
                  }}>
                    {result.perf_output_before}
                  </div>
                </div>
              )}

              {(result.perf_output_after && hasExtraOutput(result.perf_output_after)) && (
                <div style={{
                  background: "rgba(165, 28, 48, 0.05)",
                  padding: "20px",
                  borderRadius: "16px",
                  border: "1px solid rgba(165, 28, 48, 0.2)"
                }}>
                  <div style={{fontWeight: "600", color: "#A51C30", marginBottom: "12px", display: "flex", justifyContent: "space-between", alignItems: "center"}}>
                    <span>📋 Results After Patch (Full Output):</span>
                    <button 
                      onClick={() => copyToClipboard(result.perf_output_after)}
                      style={{
                        padding: "6px 12px",
                        background: "#A51C30",
                        color: "white",
                        border: "none",
                        borderRadius: "6px",
                        fontSize: "12px",
                        cursor: "pointer",
                        transition: "all 0.2s ease"
                      }}
                      onMouseOver={(e) => e.target.style.background = "#8B0000"}
                      onMouseOut={(e) => e.target.style.background = "#A51C30"}
                    >
                      📋 Copy
                    </button>
                  </div>
                  <div style={{
                    background: "white",
                    padding: "16px",
                    borderRadius: "12px",
                    fontSize: "13px",
                    fontFamily: "'Fira Code', monospace",
                    border: "1px solid #e2e8f0",
                    overflowX: "auto",
                    whiteSpace: "pre-wrap",
                    maxHeight: "300px",
                    overflowY: "auto"
                  }}>
                    {result.perf_output_after}
                  </div>
                </div>
              )}

              {result.ratio && (
                <div style={{
                  background: "rgba(165, 28, 48, 0.05)",
                  padding: "20px",
                  borderRadius: "16px",
                  border: "1px solid rgba(165, 28, 48, 0.2)"
                }}>
                  <div style={{fontWeight: "600", color: "#A51C30", marginBottom: "12px", display: "flex", justifyContent: "space-between", alignItems: "center"}}>
                    <span>📈 Performance Comparison:</span>
                    <button 
                      onClick={() => copyToClipboard(`Performance Ratio: ${result.ratio.toFixed(4)} (${result.ratio < 1 ? "Faster" : result.ratio > 1 ? "Slower" : "Same"})`)}
                      style={{
                        padding: "6px 12px",
                        background: "#A51C30",
                        color: "white",
                        border: "none",
                        borderRadius: "6px",
                        fontSize: "12px",
                        cursor: "pointer",
                        transition: "all 0.2s ease"
                      }}
                      onMouseOver={(e) => e.target.style.background = "#8B0000"}
                      onMouseOut={(e) => e.target.style.background = "#A51C30"}
                    >
                      📋 Copy
                    </button>
                  </div>
                  <div style={{
                    padding: "16px",
                    borderRadius: "12px",
                    fontSize: "16px",
                    fontWeight: "700",
                    textAlign: "center",
                    background: highlightColor(result.ratio) === "green" ? "rgba(5, 150, 105, 0.1)" :
                                highlightColor(result.ratio) === "red" ? "rgba(220, 38, 38, 0.1)" : "rgba(107, 114, 128, 0.1)",
                    color: highlightColor(result.ratio) === "green" ? "#059669" :
                           highlightColor(result.ratio) === "red" ? "#DC2626" : "#6B7280",
                    border: "2px solid",
                    borderColor: highlightColor(result.ratio) === "green" ? "rgba(5, 150, 105, 0.3)" :
                                 highlightColor(result.ratio) === "red" ? "rgba(220, 38, 38, 0.3)" : "rgba(107, 114, 128, 0.3)"
                  }}>
                    Performance Ratio: {result.ratio.toFixed(4)} ({result.ratio < 1 ? "🚀 Faster" : result.ratio > 1 ? "🐌 Slower" : "➡️ Same"})
                  </div>
                </div>
              )}

              {result.error && (
                <div style={{
                  background: "rgba(220, 38, 38, 0.1)",
                  padding: "20px",
                  borderRadius: "16px",
                  border: "2px solid rgba(220, 38, 38, 0.3)",
                  boxShadow: "0 4px 15px rgba(220, 38, 38, 0.2)"
                }}>
                  <div style={{fontWeight: "600", color: "#DC2626", marginBottom: "8px"}}>
                    ❌ Error:
                  </div>
                  <div style={{color: "#DC2626", fontSize: "14px"}}>
                    {result.error}
                  </div>
                </div>
              )}
            </div>
          </div>
        )}

        {error && (
          <div style={{
            background: "rgba(220, 38, 38, 0.1)",
            padding: "20px",
            borderRadius: "16px",
            border: "2px solid rgba(220, 38, 38, 0.3)",
            boxShadow: "0 4px 15px rgba(220, 38, 38, 0.2)",
            marginTop: "20px"
          }}>
            <div style={{fontWeight: "600", color: "#DC2626", marginBottom: "8px"}}>
              ❌ Error:
            </div>
            <div style={{color: "#DC2626", fontSize: "14px"}}>
              {error}
            </div>
          </div>
        )}
      </div>

      {/* Footer */}
      <div style={{
        background: "white",
        borderTop: "1px solid rgba(165, 28, 48, 0.2)",
        padding: "20px 0",
        marginTop: "40px"
      }}>
        <div style={{maxWidth: 1200, margin: "0 auto", padding: "0 20px", textAlign: "center"}}>
          <p style={{
            margin: 0,
            fontSize: "14px",
            color: "#666",
            fontWeight: "500"
          }}>
            © 2024 Edge Computing Lab, Harvard John A. Paulson School of Engineering and Applied Sciences
          </p>
        </div>
      </div>
    </div>
  );
};

export default App;
