import React, { useState } from "react";

const defaultImports = `import numpy as np
import astropy.units as u
from astropy.coordinates.sky_coordinate import SkyCoord`;
const defaultSetup = `global c
c = SkyCoord(np.arange(30.)*u.deg, np.arange(30.)*u.deg)`;
const defaultWorkload = `global c
c[1:2]`;

function highlightColor(ratio: number) {
  if (ratio < 1) return "green";
  if (ratio > 1) return "red";
  return "grey";
}

const App = () => {
  const [prUrl, setPrUrl] = useState("https://github.com/astropy/astropy/pull/6940");
  const [imports, setImports] = useState(defaultImports);
  const [setup, setSetup] = useState(defaultSetup);
  const [workload, setWorkload] = useState(defaultWorkload);
  const [number, setNumber] = useState(100);
  const [repeat, setRepeat] = useState(200);
  const [running, setRunning] = useState(false);
  const [result, setResult] = useState(null);
  const [error, setError] = useState(null);
  const [dockerStatus, setDockerStatus] = useState(null);
  const [checkingDocker, setCheckingDocker] = useState(false);

  const checkDockerStatus = async () => {
    setCheckingDocker(true);
    try {
      const res = await fetch("http://localhost:5679/check_docker");
      const data = await res.json();
      setDockerStatus(data);
    } catch (e) {
      setDockerStatus({
        installed: false,
        running: false,
        permissions: false,
        message: "Cannot connect to Docker check service",
        setup_instructions: ["Please ensure Docker permission check service is running"]
      });
    } finally {
      setCheckingDocker(false);
    }
  };

  const handleRun = async () => {
    if (!dockerStatus?.permissions) {
      setError("Docker permissions not configured, please check Docker status first");
      return;
    }

    setRunning(true);
    setResult(null);
    setError(null);
    try {
      const res = await fetch("http://localhost:5678/run_benchmark", {
        method: "POST",
        headers: {"Content-Type": "application/json"},
        body: JSON.stringify({
          pr_url: prUrl,
          imports,
          setup,
          workload,
          number,
          repeat,
        }),
      });
      const data = await res.json();
      setResult(data);
    } catch (e) {
      setError("Run failed: " + e);
    } finally {
      setRunning(false);
    }
  };

  return (
    <div style={{
      minHeight: "100vh",
      background: "linear-gradient(135deg, #667eea 0%, #764ba2 100%)",
      fontFamily: "'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif"
    }}>
      {/* Header with Logo */}
      <div style={{
        background: "rgba(255, 255, 255, 0.95)",
        backdropFilter: "blur(10px)",
        borderBottom: "1px solid rgba(255, 255, 255, 0.2)",
        padding: "20px 0",
        boxShadow: "0 4px 20px rgba(0, 0, 0, 0.1)"
      }}>
        <div style={{maxWidth: 1200, margin: "0 auto", padding: "0 20px", display: "flex", alignItems: "center", justifyContent: "space-between"}}>
          <div style={{display: "flex", alignItems: "center", gap: "15px"}}>
            <div style={{
              width: "50px",
              height: "50px",
              background: "linear-gradient(45deg, #667eea, #764ba2)",
              borderRadius: "12px",
              display: "flex",
              alignItems: "center",
              justifyContent: "center",
              color: "white",
              fontSize: "24px",
              fontWeight: "bold",
              boxShadow: "0 4px 15px rgba(102, 126, 234, 0.4)"
            }}>
              EC
            </div>
            <div>
              <h1 style={{
                margin: 0,
                fontSize: "28px",
                fontWeight: "700",
                background: "linear-gradient(45deg, #667eea, #764ba2)",
                WebkitBackgroundClip: "text",
                WebkitTextFillColor: "transparent",
                backgroundClip: "text"
              }}>
                Edge Computing Lab
              </h1>
              <p style={{
                margin: "4px 0 0 0",
                fontSize: "14px",
                color: "#666",
                fontWeight: "500"
              }}>
                Performance Benchmark Platform
              </p>
            </div>
          </div>
          <div style={{
            fontSize: "12px",
            color: "#888",
            textAlign: "right",
            fontStyle: "italic"
          }}>
            Research Property
          </div>
        </div>
      </div>

      {/* Main Content */}
      <div style={{maxWidth: 1200, margin: "0 auto", padding: "40px 20px"}}>
        {/* Docker Status Check Section */}
        <div style={{
          background: "rgba(255, 255, 255, 0.95)",
          backdropFilter: "blur(10px)",
          borderRadius: "20px",
          padding: "30px",
          marginBottom: "30px",
          boxShadow: "0 8px 32px rgba(0, 0, 0, 0.1)",
          border: "1px solid rgba(255, 255, 255, 0.2)"
        }}>
          <h3 style={{
            margin: "0 0 20px 0",
            fontSize: "20px",
            fontWeight: "600",
            color: "#333"
          }}>
            🔧 Docker Status Check
          </h3>
          <button 
            onClick={checkDockerStatus} 
            disabled={checkingDocker}
            style={{
              padding: "12px 24px",
              marginBottom: "20px",
              background: checkingDocker ? "#e0e0e0" : "linear-gradient(45deg, #667eea, #764ba2)",
              color: "white",
              border: "none",
              borderRadius: "12px",
              fontSize: "14px",
              fontWeight: "600",
              cursor: checkingDocker ? "not-allowed" : "pointer",
              transition: "all 0.3s ease",
              boxShadow: checkingDocker ? "none" : "0 4px 15px rgba(102, 126, 234, 0.4)"
            }}
          >
            {checkingDocker ? "Checking..." : "Check Docker Status"}
          </button>

          {dockerStatus && (
            <div>
              <div style={{marginBottom: "15px"}}>
                <strong style={{color: "#333"}}>Status: </strong>
                <span style={{
                  color: dockerStatus.permissions ? "#10b981" : "#ef4444",
                  fontWeight: "600",
                  padding: "4px 12px",
                  borderRadius: "20px",
                  background: dockerStatus.permissions ? "rgba(16, 185, 129, 0.1)" : "rgba(239, 68, 68, 0.1)",
                  fontSize: "14px"
                }}>
                  {dockerStatus.permissions ? "✅ Normal" : "❌ Configuration Required"}
                </span>
              </div>
              <div style={{marginBottom: "15px", color: "#666"}}>
                <strong>Message: </strong>{dockerStatus.message}
              </div>
              {dockerStatus.setup_instructions.length > 0 && (
                <div style={{
                  background: "rgba(255, 193, 7, 0.1)",
                  padding: "20px",
                  borderRadius: "12px",
                  border: "1px solid rgba(255, 193, 7, 0.3)"
                }}>
                  <strong style={{color: "#d97706"}}>Setup Instructions:</strong>
                  <ul style={{margin: "10px 0", paddingLeft: "20px", color: "#666"}}>
                    {dockerStatus.setup_instructions.map((instruction, index) => (
                      <li key={index} style={{marginBottom: "5px"}}>{instruction}</li>
                    ))}
                  </ul>
                </div>
              )}
            </div>
          )}
        </div>

        {/* Main Form */}
        <div style={{
          background: "rgba(255, 255, 255, 0.95)",
          backdropFilter: "blur(10px)",
          borderRadius: "20px",
          padding: "40px",
          marginBottom: "30px",
          boxShadow: "0 8px 32px rgba(0, 0, 0, 0.1)",
          border: "1px solid rgba(255, 255, 255, 0.2)"
        }}>
          <h2 style={{
            margin: "0 0 30px 0",
            fontSize: "24px",
            fontWeight: "700",
            color: "#333",
            textAlign: "center"
          }}>
            🚀 Performance Benchmark Configuration
          </h2>

          <div style={{display: "grid", gap: "25px"}}>
            <div>
              <label style={{
                display: "block",
                marginBottom: "8px",
                fontSize: "14px",
                fontWeight: "600",
                color: "#333"
              }}>
                GitHub PR URL:
              </label>
              <input 
                style={{
                  width: "100%",
                  padding: "12px 16px",
                  border: "2px solid #e5e7eb",
                  borderRadius: "12px",
                  fontSize: "14px",
                  transition: "all 0.3s ease",
                  background: running ? "#f9fafb" : "white",
                  opacity: running ? 0.6 : 1
                }}
                value={prUrl} 
                onChange={e=>setPrUrl(e.target.value)} 
                disabled={running}
                placeholder="https://github.com/org/repo/pull/123"
              />
            </div>

            <div style={{display: "grid", gridTemplateColumns: "1fr 1fr", gap: "20px"}}>
              <div>
                <label style={{
                  display: "block",
                  marginBottom: "8px",
                  fontSize: "14px",
                  fontWeight: "600",
                  color: "#333"
                }}>
                  Imports:
                </label>
                <textarea 
                  rows={4}
                  style={{
                    width: "100%",
                    padding: "12px 16px",
                    border: "2px solid #e5e7eb",
                    borderRadius: "12px",
                    fontSize: "14px",
                    fontFamily: "'Fira Code', 'Monaco', 'Consolas', monospace",
                    resize: "vertical",
                    transition: "all 0.3s ease",
                    background: running ? "#f9fafb" : "white",
                    opacity: running ? 0.6 : 1
                  }}
                  value={imports} 
                  onChange={e=>setImports(e.target.value)} 
                  disabled={running}
                  placeholder="import numpy as np"
                />
              </div>
              <div>
                <label style={{
                  display: "block",
                  marginBottom: "8px",
                  fontSize: "14px",
                  fontWeight: "600",
                  color: "#333"
                }}>
                  setup():
                </label>
                <textarea 
                  rows={4}
                  style={{
                    width: "100%",
                    padding: "12px 16px",
                    border: "2px solid #e5e7eb",
                    borderRadius: "12px",
                    fontSize: "14px",
                    fontFamily: "'Fira Code', 'Monaco', 'Consolas', monospace",
                    resize: "vertical",
                    transition: "all 0.3s ease",
                    background: running ? "#f9fafb" : "white",
                    opacity: running ? 0.6 : 1
                  }}
                  value={setup} 
                  onChange={e=>setSetup(e.target.value)} 
                  disabled={running}
                  placeholder="global c\nc = np.arange(100)"
                />
              </div>
            </div>

            <div>
              <label style={{
                display: "block",
                marginBottom: "8px",
                fontSize: "14px",
                fontWeight: "600",
                color: "#333"
              }}>
                workload():
              </label>
              <textarea 
                rows={4}
                style={{
                  width: "100%",
                  padding: "12px 16px",
                  border: "2px solid #e5e7eb",
                  borderRadius: "12px",
                  fontSize: "14px",
                  fontFamily: "'Fira Code', 'Monaco', 'Consolas', monospace",
                  resize: "vertical",
                  transition: "all 0.3s ease",
                  background: running ? "#f9fafb" : "white",
                  opacity: running ? 0.6 : 1
                }}
                value={workload} 
                onChange={e=>setWorkload(e.target.value)} 
                disabled={running}
                placeholder="global c\nc * 2"
              />
            </div>

            <div style={{display: "flex", gap: "20px", alignItems: "center"}}>
              <div style={{flex: 1}}>
                <label style={{
                  display: "block",
                  marginBottom: "8px",
                  fontSize: "14px",
                  fontWeight: "600",
                  color: "#333"
                }}>
                  Number:
                </label>
                <input 
                  type="number"
                  style={{
                    width: "100%",
                    padding: "12px 16px",
                    border: "2px solid #e5e7eb",
                    borderRadius: "12px",
                    fontSize: "14px",
                    transition: "all 0.3s ease",
                    background: running ? "#f9fafb" : "white",
                    opacity: running ? 0.6 : 1
                  }}
                  value={number} 
                  onChange={e=>setNumber(Number(e.target.value))} 
                  disabled={running}
                />
              </div>
              <div style={{flex: 1}}>
                <label style={{
                  display: "block",
                  marginBottom: "8px",
                  fontSize: "14px",
                  fontWeight: "600",
                  color: "#333"
                }}>
                  Repeat:
                </label>
                <input 
                  type="number"
                  style={{
                    width: "100%",
                    padding: "12px 16px",
                    border: "2px solid #e5e7eb",
                    borderRadius: "12px",
                    fontSize: "14px",
                    transition: "all 0.3s ease",
                    background: running ? "#f9fafb" : "white",
                    opacity: running ? 0.6 : 1
                  }}
                  value={repeat} 
                  onChange={e=>setRepeat(Number(e.target.value))} 
                  disabled={running}
                />
              </div>
            </div>

            <div style={{textAlign: "center", marginTop: "20px"}}>
              <button 
                style={{
                  padding: "16px 40px",
                  background: running ? "#e0e0e0" : "linear-gradient(45deg, #667eea, #764ba2)",
                  color: "white",
                  border: "none",
                  borderRadius: "16px",
                  fontSize: "16px",
                  fontWeight: "700",
                  cursor: running ? "not-allowed" : "pointer",
                  transition: "all 0.3s ease",
                  boxShadow: running ? "none" : "0 8px 25px rgba(102, 126, 234, 0.4)",
                  minWidth: "200px"
                }}
                onClick={handleRun} 
                disabled={running}
              >
                {running ? "⏳ Running..." : "🚀 Run Benchmark"}
              </button>
            </div>
          </div>
        </div>

        {/* Results Section */}
        {result && (
          <div style={{
            background: "rgba(255, 255, 255, 0.95)",
            backdropFilter: "blur(10px)",
            borderRadius: "20px",
            padding: "40px",
            marginBottom: "30px",
            boxShadow: "0 8px 32px rgba(0, 0, 0, 0.1)",
            border: "1px solid rgba(255, 255, 255, 0.2)"
          }}>
            <h3 style={{
              margin: "0 0 30px 0",
              fontSize: "22px",
              fontWeight: "700",
              color: "#333",
              textAlign: "center"
            }}>
              📊 Benchmark Results
            </h3>

            <div style={{display: "grid", gap: "25px"}}>
              <div style={{
                background: "rgba(102, 126, 234, 0.05)",
                padding: "20px",
                borderRadius: "16px",
                border: "1px solid rgba(102, 126, 234, 0.2)"
              }}>
                <div style={{fontWeight: "600", color: "#667eea", marginBottom: "8px"}}>
                  🐳 Image Name:
                </div>
                <div style={{fontFamily: "'Fira Code', monospace", fontSize: "14px"}}>
                  {result.image_tag}
                </div>
              </div>

              <div style={{
                background: "rgba(59, 130, 246, 0.05)",
                padding: "20px",
                borderRadius: "16px",
                border: "1px solid rgba(59, 130, 246, 0.2)"
              }}>
                <div style={{fontWeight: "600", color: "#3b82f6", marginBottom: "12px"}}>
                  📋 Complete Docker Command:
                </div>
                <div style={{
                  background: "#f8fafc",
                  padding: "16px",
                  borderRadius: "12px",
                  fontSize: "13px",
                  fontFamily: "'Fira Code', monospace",
                  border: "1px solid #e2e8f0",
                  overflowX: "auto"
                }}>
                  {result.docker_command}
                </div>
                <div style={{fontSize: "12px", color: "#64748b", marginTop: "8px"}}>
                  Note: Replace &lt;REPLACE_ME&gt; with the full path to your workload.py file
                </div>
              </div>

              <div style={{
                background: "rgba(34, 197, 94, 0.05)",
                padding: "20px",
                borderRadius: "16px",
                border: "1px solid rgba(34, 197, 94, 0.2)"
              }}>
                <div style={{fontWeight: "600", color: "#22c55e", marginBottom: "12px"}}>
                  📝 Auto-generated workload.py:
                </div>
                <pre style={{
                  background: "#f8fafc",
                  padding: "16px",
                  borderRadius: "12px",
                  fontSize: "13px",
                  fontFamily: "'Fira Code', monospace",
                  border: "1px solid #e2e8f0",
                  overflowX: "auto",
                  margin: 0
                }}>
                  {result.workload_code}
                </pre>
              </div>

              {result.download_info && (
                <div style={{
                  background: "rgba(59, 130, 246, 0.05)",
                  padding: "20px",
                  borderRadius: "16px",
                  border: "1px solid rgba(59, 130, 246, 0.2)"
                }}>
                  <div style={{fontWeight: "600", color: "#3b82f6", marginBottom: "12px"}}>
                    📥 Image Download Info:
                  </div>
                  <div style={{
                    background: "rgba(59, 130, 246, 0.1)",
                    padding: "16px",
                    borderRadius: "12px",
                    fontSize: "13px",
                    fontFamily: "'Fira Code', monospace",
                    border: "1px solid rgba(59, 130, 246, 0.3)"
                  }}>
                    <div style={{color: "#1d4ed8", fontWeight: "bold", marginBottom: "8px"}}>
                      📥 New Image Downloaded
                    </div>
                    <pre style={{
                      margin: 0,
                      whiteSpace: "pre-wrap",
                      fontSize: "12px"
                    }}>
                      {result.download_info}
                    </pre>
                  </div>
                </div>
              )}

              <div style={{display: "grid", gridTemplateColumns: "1fr 1fr", gap: "20px"}}>
                <div style={{
                  background: "rgba(34, 197, 94, 0.05)",
                  padding: "20px",
                  borderRadius: "16px",
                  border: "1px solid rgba(34, 197, 94, 0.2)"
                }}>
                  <div style={{fontWeight: "600", color: "#22c55e", marginBottom: "12px"}}>
                    📈 Initial Results:
                  </div>
                  {result.mean_before && result.std_before ? (
                    <div style={{
                      background: "rgba(34, 197, 94, 0.1)",
                      padding: "16px",
                      borderRadius: "12px",
                      border: "1px solid rgba(34, 197, 94, 0.3)"
                    }}>
                      <div style={{fontSize: "14px", fontWeight: "600"}}>
                        Mean: {result.mean_before}
                      </div>
                      <div style={{fontSize: "14px", fontWeight: "600"}}>
                        Std: {result.std_before}
                      </div>
                    </div>
                  ) : (
                    <div style={{color: "#ef4444", fontWeight: "bold"}}>
                      {result.before}
                    </div>
                  )}
                </div>

                <div style={{
                  background: "rgba(16, 185, 129, 0.05)",
                  padding: "20px",
                  borderRadius: "16px",
                  border: "2px solid #10b981"
                }}>
                  <div style={{fontWeight: "600", color: "#10b981", marginBottom: "12px"}}>
                    🎯 Results After Patch:
                  </div>
                  {result.mean_after && result.std_after ? (
                    <div style={{
                      background: "rgba(16, 185, 129, 0.1)",
                      padding: "16px",
                      borderRadius: "12px",
                      border: "1px solid rgba(16, 185, 129, 0.3)"
                    }}>
                      <div style={{fontSize: "14px", fontWeight: "600"}}>
                        Mean: {result.mean_after}
                      </div>
                      <div style={{fontSize: "14px", fontWeight: "600"}}>
                        Std: {result.std_after}
                      </div>
                    </div>
                  ) : (
                    <div style={{color: "#ef4444", fontWeight: "bold"}}>
                      {result.after}
                    </div>
                  )}
                </div>
              </div>

              {result.ratio && (
                <div style={{
                  padding: "20px",
                  borderRadius: "16px",
                  background: highlightColor(result.ratio) === "green" ? "rgba(34, 197, 94, 0.1)" :
                              highlightColor(result.ratio) === "red" ? "rgba(239, 68, 68, 0.1)" : "rgba(156, 163, 175, 0.1)",
                  border: "2px solid",
                  borderColor: highlightColor(result.ratio) === "green" ? "#22c55e" :
                              highlightColor(result.ratio) === "red" ? "#ef4444" : "#9ca3af",
                  textAlign: "center"
                }}>
                  <div style={{
                    fontSize: "18px",
                    fontWeight: "700",
                    color: highlightColor(result.ratio) === "green" ? "#22c55e" :
                           highlightColor(result.ratio) === "red" ? "#ef4444" : "#6b7280"
                  }}>
                    Performance Change Ratio: {result.ratio.toFixed(4)}
                  </div>
                  <div style={{
                    fontSize: "16px",
                    fontWeight: "600",
                    color: highlightColor(result.ratio) === "green" ? "#22c55e" :
                           highlightColor(result.ratio) === "red" ? "#ef4444" : "#6b7280",
                    marginTop: "8px"
                  }}>
                    {result.ratio < 1 ? "🚀 Faster" : result.ratio > 1 ? "🐌 Slower" : "⚖️ Same"}
                  </div>
                </div>
              )}

              {result.error && (
                <div style={{
                  background: "rgba(239, 68, 68, 0.1)",
                  padding: "20px",
                  borderRadius: "16px",
                  border: "1px solid rgba(239, 68, 68, 0.3)",
                  color: "#ef4444",
                  fontWeight: "600"
                }}>
                  ❌ Error: {result.error}
                </div>
              )}
            </div>
          </div>
        )}

        {error && (
          <div style={{
            background: "rgba(239, 68, 68, 0.1)",
            padding: "20px",
            borderRadius: "16px",
            border: "1px solid rgba(239, 68, 68, 0.3)",
            color: "#ef4444",
            fontWeight: "600",
            textAlign: "center"
          }}>
            ❌ {error}
          </div>
        )}
      </div>

      {/* Footer */}
      <div style={{
        background: "rgba(255, 255, 255, 0.95)",
        backdropFilter: "blur(10px)",
        borderTop: "1px solid rgba(255, 255, 255, 0.2)",
        padding: "20px 0",
        textAlign: "center",
        color: "#666",
        fontSize: "14px"
      }}>
        <div style={{maxWidth: 1200, margin: "0 auto", padding: "0 20px"}}>
          © 2024 Edge Computing Lab. All rights reserved.
        </div>
      </div>
    </div>
  );
};

export default App;
