import React, { useState } from "react";

const defaultImports = `import numpy as np
import astropy.units as u
from astropy.coordinates.sky_coordinate import SkyCoord`;
const defaultSetup = `global c
c = SkyCoord(np.arange(30.)*u.deg, np.arange(30.)*u.deg)`;
const defaultWorkload = `global c
c[1:2]`;

function highlightColor(ratio: number) {
  if (ratio < 1) return "green";
  if (ratio > 1) return "red";
  return "grey";
}

const App = () => {
  const [prUrl, setPrUrl] = useState("https://github.com/astropy/astropy/pull/6940");
  const [imports, setImports] = useState(defaultImports);
  const [setup, setSetup] = useState(defaultSetup);
  const [workload, setWorkload] = useState(defaultWorkload);
  const [number, setNumber] = useState(100);
  const [repeat, setRepeat] = useState(200);
  const [running, setRunning] = useState(false);
  const [result, setResult] = useState(null);
  const [error, setError] = useState(null);
  const [dockerStatus, setDockerStatus] = useState(null);
  const [checkingDocker, setCheckingDocker] = useState(false);

  const checkDockerStatus = async () => {
    setCheckingDocker(true);
    try {
      const res = await fetch("http://localhost:5679/check_docker");
      const data = await res.json();
      setDockerStatus(data);
    } catch (e) {
      setDockerStatus({
        installed: false,
        running: false,
        permissions: false,
        message: "无法连接到Docker检查服务",
        setup_instructions: ["请确保Docker权限检查服务正在运行"]
      });
    } finally {
      setCheckingDocker(false);
    }
  };

  const handleRun = async () => {
    if (!dockerStatus?.permissions) {
      setError("Docker权限未配置，请先检查Docker状态");
      return;
    }

    setRunning(true);
    setResult(null);
    setError(null);
    try {
      const res = await fetch("http://localhost:5678/run_benchmark", {
        method: "POST",
        headers: {"Content-Type": "application/json"},
        body: JSON.stringify({
          pr_url: prUrl,
          imports,
          setup,
          workload,
          number,
          repeat,
        }),
      });
      const data = await res.json();
      setResult(data);
    } catch (e) {
      setError("运行失败: " + e);
    } finally {
      setRunning(false);
    }
  };

  return (
    <div style={{maxWidth: 900, margin: "0 auto", fontFamily: "monospace"}}>
      <h2>自动性能基准测试 Web</h2>

      {/* Docker状态检查区域 */}
      <div style={{marginBottom: 20, padding: 16, border: "1px solid #ddd", borderRadius: 8}}>
        <h3>Docker状态检查</h3>
        <button 
          onClick={checkDockerStatus} 
          disabled={checkingDocker}
          style={{padding: 8, marginBottom: 10}}
        >
          {checkingDocker ? "检查中..." : "检查Docker状态"}
        </button>

        {dockerStatus && (
          <div>
            <div style={{marginBottom: 10}}>
              <strong>状态: </strong>
              <span style={{
                color: dockerStatus.permissions ? "green" : "red",
                fontWeight: "bold"
              }}>
                {dockerStatus.permissions ? "✅ 正常" : "❌ 需要配置"}
              </span>
            </div>
            <div style={{marginBottom: 10}}>
              <strong>消息: </strong>{dockerStatus.message}
            </div>
            {dockerStatus.setup_instructions.length > 0 && (
              <div style={{background: "#fff3cd", padding: 10, borderRadius: 4}}>
                <strong>配置步骤:</strong>
                <ul style={{margin: "5px 0", paddingLeft: 20}}>
                  {dockerStatus.setup_instructions.map((instruction, index) => (
                    <li key={index}>{instruction}</li>
                  ))}
                </ul>
              </div>
            )}
          </div>
        )}
      </div>
      <div>
        <label>GitHub PR 地址:</label><br/>
        <input style={{width: "100%"}} value={prUrl} onChange={e=>setPrUrl(e.target.value)} disabled={running}/>
      </div>
      <div>
        <label>Imports:</label><br/>
        <textarea rows={3} style={{width: "100%"}} value={imports} onChange={e=>setImports(e.target.value)} disabled={running}/>
      </div>
      <div>
        <label>setup():</label><br/>
        <textarea rows={3} style={{width: "100%"}} value={setup} onChange={e=>setSetup(e.target.value)} disabled={running}/>
      </div>
      <div>
        <label>workload():</label><br/>
        <textarea rows={3} style={{width: "100%"}} value={workload} onChange={e=>setWorkload(e.target.value)} disabled={running}/>
      </div>
      <div>
        <label>number:</label>
        <input type="number" value={number} onChange={e=>setNumber(Number(e.target.value))} disabled={running}/>
        <label style={{marginLeft: 10}}>repeat:</label>
        <input type="number" value={repeat} onChange={e=>setRepeat(Number(e.target.value))} disabled={running}/>
      </div>
      <button style={{marginTop: 16, padding: 8, fontWeight: 700}} onClick={handleRun} disabled={running}>
        {running ? "运行中..." : "运行"}
      </button>
      {result &&
        <div style={{marginTop: 30, border: "1px solid #ddd", borderRadius: 8, padding: 16}}>
          <div><b>镜像名: </b>{result.image_tag}</div>
          <div>
            <b>自动生成 workload.py:</b>
            <pre style={{background: "#f9f9f9", padding: 10, fontSize: 13}}>{result.workload_code}</pre>
          </div>
          <div>
            <b>执行的终端命令:</b>
            <div style={{background: "#f0f0f0", padding: 10, fontSize: 13, fontFamily: "monospace"}}>
              {result.executed_commands && result.executed_commands.map((cmd, index) => (
                <div key={index} style={{marginBottom: 5}}>
                  <span style={{color: "#666"}}>$ </span>{cmd}
                </div>
              ))}
            </div>
          </div>
          <div>
            <b>首次结果:</b>
            {result.mean_before && result.std_before ? (
              <div style={{background: "#e7f7e7", color: "#222", border: "1px solid #b0ebb0", padding: 10}}>
                Mean: {result.mean_before}<br/>
                Std: {result.std_before}
              </div>
            ) : (
              <div style={{color: "red", fontWeight: "bold"}}>
                {result.before}
              </div>
            )}
          </div>
          <div>
            <b>Patch后结果:</b>
            {result.mean_after && result.std_after ? (
              <div style={{background: "#f7e7e7", color: "#222", border: "1px solid #ebb0b0", padding: 10}}>
                Mean: {result.mean_after}<br/>
                Std: {result.std_after}
              </div>
            ) : (
              <div style={{color: "red", fontWeight: "bold"}}>
                {result.after}
              </div>
            )}
          </div>
          {result.ratio &&
            <div style={{
              marginTop: 8,
              padding: 12,
              background: highlightColor(result.ratio) === "green" ? "#e0ffe0" :
                          highlightColor(result.ratio) === "red" ? "#ffe0e0" : "#f0f0f0",
              color: "#333",
              fontWeight: 700,
              borderRadius: 4,
              border: "2px solid",
              borderColor: highlightColor(result.ratio) === "green" ? "#00bb00" :
                          highlightColor(result.ratio) === "red" ? "#ff5555" : "#bbb"
            }}>
              性能变化 Ratio: {result.ratio.toFixed(4)} ({result.ratio < 1 ? "更快" : result.ratio > 1 ? "变慢" : "持平"})
            </div>
          }
          {result.error &&
            <div style={{marginTop: 10, color: "red", fontWeight: 700}}>
              错误: {result.error}
            </div>
          }
        </div>
      }
      {error &&
        <div style={{marginTop: 16, color: "red", fontWeight: 700}}>
          {error}
        </div>
      }
    </div>
  );
};

export default App;
