import React, { useState } from "react";

const defaultImports = ``;
const defaultSetup = ``;
const defaultWorkload = ``;

function highlightColor(ratio: number) {
  if (ratio < 1) return "green";
  if (ratio > 1) return "red";
  return "grey";
}

const App = () => {
  const [prUrl, setPrUrl] = useState("");
  const [imports, setImports] = useState(defaultImports);
  const [setup, setSetup] = useState(defaultSetup);
  const [workload, setWorkload] = useState(defaultWorkload);
  const [number, setNumber] = useState("");
  const [repeat, setRepeat] = useState("");
  const [running, setRunning] = useState(false);
  const [result, setResult] = useState(null);
  const [error, setError] = useState(null);
  const [dockerStatus, setDockerStatus] = useState(null);
  const [checkingDocker, setCheckingDocker] = useState(false);

  const copyToClipboard = async (text) => {
    try {
      await navigator.clipboard.writeText(text);
      // 可以添加一个临时的成功提示
    } catch (err) {
      console.error('Failed to copy: ', err);
    }
  };

  const clearAllInputs = () => {
    setPrUrl("https://github.com/astropy/astropy/pull/6940");
    setImports(defaultImports);
    setSetup(defaultSetup);
    setWorkload(defaultWorkload);
    setNumber(100);
    setRepeat(200);
    // 不清除 result 和 error，保持输出显示
  };

  const checkDockerStatus = async () => {
    setCheckingDocker(true);
    try {
      const res = await fetch("http://localhost:5679/check_docker");
      const data = await res.json();
      setDockerStatus(data);
    } catch (e) {
      setDockerStatus({
        installed: false,
        running: false,
        permissions: false,
        message: "Cannot connect to Docker check service",
        setup_instructions: ["Please ensure Docker permission check service is running"]
      });
    } finally {
      setCheckingDocker(false);
    }
  };

  const handleRun = async () => {
