                    <button 
                      onClick={() => copyToClipboard(result.mean_after && result.std_after ? 
                        `Mean: ${result.mean_after}\nStd: ${result.std_after}` : result.after)}
                      style={{
                        padding: "6px 12px",
                        background: "#A51C30",
                        color: "white",
                        border: "none",
                        borderRadius: "6px",
                        fontSize: "12px",
                        cursor: "pointer",
                        transition: "all 0.2s ease"
                      }}
                      onMouseOver={(e) => e.target.style.background = "#8B0000"}
                      onMouseOut={(e) => e.target.style.background = "#A51C30"}
                    >
                      📋 Copy
                    </button>
                  </div>
                  {result.mean_after && result.std_after ? (
                    <div style={{
                      background: "white",
                      padding: "16px",
                      borderRadius: "12px",
                      border: "1px solid #e2e8f0"
                    }}>
                      <div style={{fontSize: "14px", fontWeight: "600"}}>
                        Mean: {result.mean_after}
                      </div>
                      <div style={{fontSize: "14px", fontWeight: "600"}}>
                        Std: {result.std_after}
                      </div>
                    </div>
                  ) : (
                    <div style={{color: "#DC2626", fontWeight: "bold"}}>
                      {result.after}
                    </div>
                  )}
                </div>
              </div>

              {result.ratio && (
                <div style={{
                  padding: "20px",
                  borderRadius: "16px",
                  background: highlightColor(result.ratio) === "green" ? "rgba(34, 197, 94, 0.1)" :
                              highlightColor(result.ratio) === "red" ? "rgba(239, 68, 68, 0.1)" : "rgba(156, 163, 175, 0.1)",
                  border: "2px solid",
                  borderColor: highlightColor(result.ratio) === "green" ? "#22c55e" :
                              highlightColor(result.ratio) === "red" ? "#ef4444" : "#9ca3af",
                  textAlign: "center"
                }}>
                  <div style={{
                    fontSize: "18px",
                    fontWeight: "700",
                    color: highlightColor(result.ratio) === "green" ? "#22c55e" :
                           highlightColor(result.ratio) === "red" ? "#ef4444" : "#6b7280"
                  }}>
                    Performance Change Ratio: {result.ratio.toFixed(4)}
                  </div>
                  <div style={{
                    fontSize: "16px",
                    fontWeight: "600",
                    color: highlightColor(result.ratio) === "green" ? "#22c55e" :
                           highlightColor(result.ratio) === "red" ? "#ef4444" : "#6b7280",
                    marginTop: "8px"
                  }}>
                    {result.ratio < 1 ? "🚀 Faster" : result.ratio > 1 ? "🐌 Slower" : "⚖️ Same"}
                  </div>
                </div>
              )}

              {result.error && (
                <div style={{
                  background: "white",
                  padding: "20px",
                  borderRadius: "16px",
                  border: "2px solid #ef4444",
                  color: "#ef4444",
                  fontWeight: "600",
                  boxShadow: "0 4px 12px rgba(239, 68, 68, 0.2)"
                }}>
                  ❌ Error: {result.error}
                </div>
              )}
            </div>
          </div>
        )}

        {error && (
          <div style={{
            background: "white",
            padding: "20px",
            borderRadius: "16px",
            border: "2px solid #ef4444",
            color: "#ef4444",
            fontWeight: "600",
            textAlign: "center",
            boxShadow: "0 4px 12px rgba(239, 68, 68, 0.2)"
          }}>
            ❌ {error}
          </div>
        )}
      </div>

      {/* Footer */}
      <div style={{
        background: "white",
        borderTop: "1px solid rgba(165, 28, 48, 0.2)",
        padding: "20px 0",
        textAlign: "center",
        color: "#A51C30",
        fontSize: "14px",
        fontWeight: "600"
      }}>
        <div style={{maxWidth: 1200, margin: "0 auto", padding: "0 20px"}}>
          © 2025 Edge Computing Lab, Harvard University.
        </div>
      </div>
    </div>
  );
};

export default App;
